import React, {Component} from 'react';
import { Text, View, TouchableOpacity, StyleSheet, Image} from 'react-native';
import {Camera} from 'expo-camera'
import { auth, db, storage } from '../firebase/config';

class MyCamera extends Component{
    constructor(props){
        super(props);
        this.state = {
            permission: false, 
            photo: '', 
            showCamera: true,
        }
        this.camera 
    }
componentDidMount(){
    Camera.requestCameraPermissionsAsync()
    .then(()=>{
        this.setState({
            permission: true,
        })
    })
    .catch( error => console.log(error))
}

takePicture(){
    this.camera.takePictureAsync()
    .then((photo)=>{
        this.setState({
            photo: photo.uri, 
            showCamera: false,
        })
    })
    .catch( error => console.log(error))

}
savePhoto(){
    fetch(this.state.photo)
    .then(res => res.blob()) 
    .then( image =>{
        const ref = storage.ref(`photos/${Date.now()}.jpg`)
        ref.put(image) 
            .then(()=>{
                ref.getDownloadURL() 
                    .then(url =>{
                        this.props.onImageUpload(url);
                        this.setState({
                            photo: '',
                        })
                    })
                    .catch( error => console.log(error))
            })
            .catch( error => console.log(error))
    })
    .catch( error => console.log(error))
}

clear(){
        this.setState({
            photo: '',
            showCamera: true, 
        })
    }

render(){
    return(
        <View style={styles.container}>
        {
            this.state.permission ?

                this.state.showCamera === false ? 
            <React.Fragment>
                <Image
                style={styles.cameraBody}
                source={{uri:this.state.photo}}
                />
                <View>
                <TouchableOpacity style={styles.camerabutton} onPress={()=> this.savePhoto()}>
                    <Text> Aceptar </Text> 
                </TouchableOpacity>
                <TouchableOpacity style={styles.camerabutton} onPress={()=> this.clear()}>
                    <Text> Rechazar </Text> 
                </TouchableOpacity>
                </View>
            </React.Fragment> :

            <View style={styles.container}>
                <Camera 
                    style={styles.cameraBody}
                    type={Camera.Constants.Type.back}
                    ref={(reference)=> this.camera = reference} 
                />
                <TouchableOpacity style={styles.camerabutton} onPress={()=> this.takePicture()}>
                    <Text> Sacar Foto </Text> 
                </TouchableOpacity>
            </View>
            :

            <Text>No tienes permisos para usar la c√°mara</Text>
        }
        </View>
    )
}
}
const styles = StyleSheet.create({
    container: {
        flex: 1
    },
    cameraBody: {
        flex: 7,
   },
   camerabutton: {
    flex: 1,
    justifyContent: 'center',
    },   
})

export default MyCamera;
